package cn.com.amber.remote;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;/** * 账户远程接口 * * @author yangying * @version 1.0 * @since 2022/9/8 **/@FeignClient(name="amber-account")public interface AccountFacade {    @RequestMapping(value="/account/getAccount/{accountNo}")    public String queryAccount(@PathVariable String accountNo);}